package p53;

public class SubsOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1 = 10;
		System.out.println("number1 = 10 -> "+number1);
		
		number1 += 10;
		System.out.println("number1 += 10 -> "+number1);
		
		number1 -= 10;
		System.out.println("number1 -= 10 -> "+number1);
		
		number1 *= 2;
		System.out.println("number1 *= 2 -> "+number1);
		
		number1 /= 2;
		System.out.println("number1 /= 2 -> "+number1);
		
		number1 %= 3;
		System.out.println("number1 %= 3 -> "+number1);
	}

}
